package com.syntax.class02;

public class HomeWork {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int grocery;
		grocery = 500;
		grocery += 200;
        
		System.out.println(" Total grocery expenses: "+grocery);
		
		short gas = 500;
		gas -= 60;
		System.out.println(" Total gas expenses: "+gas);
		
		double cakePiece = 11.00;
		cakePiece /=4;
	
		System.out.println(" Cake pieces for each person: "+cakePiece);
		
		int cake = 25;
		cake /= 7;
		int mod = 25 % 7;
		System.out.println(" Cake for each classmate: "+cake);
		System.out.println(" Remainder: "+mod);
		
	}

}
